mapper.select:
SELECT o.id orderId, o.pay_method payMethod, o.created_time createdTime, o.real_pay_amount realPayAmount, o.post_amount postAmount, o.is_comment isComment, os.order_status orderStatus FROM orders o LEFT JOIN order_status os on o.id = os.order_id WHERE o.user_id = #{map.userId} AND o.is_delete = 0
AND os.order_status = #{map.orderStatus}
ORDER BY o.update_time DESC
